function sortByName($a, $b) {
    return strcmp($a['name'], $b['name']);
}

function sortByAge($a, $b) {
    return $a['age'] - $b['age'];
}

function sortByBirthday($a, $b) {
    return strtotime($a['birthday']) - strtotime($b['birthday']);
}

$sortFunction = 'sortByName'; 

if (isset($_GET['sort'])) {
    $sortOption = $_GET['sort'];
    switch ($sortOption) {
        case 'name':
            $sortFunction = 'sortByName';
            break;
        case 'age':
            $sortFunction = 'sortByAge';
            break;
        case 'birthday':
            $sortFunction = 'sortByBirthday';
            break;
        default:
            $sortFunction = 'sortByName';
            break;
    }
}
usort($profiles, $sortFunction);

